import NavBar from './components/navBar/navBar.jsx';
import {useState} from "react";

const personajes = [
   {id: 1, name: "Harry Potter", house: "Griffindor"},
   {id: 2, name: "Ron Weasley", house: "Griffindor"},
   {id: 3, name: "Draco Malfoy", house: "Slytherin"},
   {id: 4, name: "Hermione Granger", house: "Griffindor"},
 ];

function App() {

   const[characters, setCharacters] = useState([]); // creamos el estado

   function searchHandler(){
      window.alert('El ID q estoy buscando');
   }

   function closeHandler(){
      window.alert('Emulamos q se cierra la card');
   }
   return (
      <div className='App'>
         <NavBar />
         <Cards 
            characters={characters} 
            onClose={closeHandler}/>

      </div>
   );
}

export default App;
